const fs = require('fs');
const path = require('path');
const rootPath = path.join(__dirname, './');

// Get the LWR Version
let version;
if (globalThis.LWR_VERSION) {
    version = globalThis.LWR_VERSION;
} else {
    version = JSON.parse(fs.readFileSync(path.join(rootPath, 'package.json'), 'utf-8')).version;
}

module.exports = {
    rootPath,
    version,
};
